  function getAmountOut(
    uint256 amountIn,
    uint256 reserveIn,
    uint256 reserveOut,
    uint256 swapFee
  ) internal view returns (uint256 amountOut) {
    require(amountIn > 0, "INSUFFICIENT_INPUT_AMOUNT");
    require(reserveIn > 0 && reserveOut > 0, "INSUFFICIENT_LIQUIDITY");

    uint256 _swapFee = (swapFee == 0) ? 25 : swapFee;
    console.log("Swap Fee: ", _swapFee);
    console.log("amountIn: ", amountIn);
    console.log("reserveIn: ", reserveIn);
    console.log("reserveOut: ", reserveOut);
    uint256 amountInWithFee = amountIn * (10000 - _swapFee);
    uint256 numerator = amountInWithFee * (reserveOut);
    uint256 denominator = reserveIn * (10000) + amountInWithFee;
    amountOut = numerator / denominator;
  }

  function checkK(KChecker memory k) public view returns (bool) {
    uint256 balance0 = k.isToken0IsIntermediaryToken
      ? (k.reserve0 + k.oldAmount)
      : (k.reserve0 - k.newAmount);
    uint256 balance1 = k.isToken0IsIntermediaryToken
      ? (k.reserve1 - k.newAmount)
      : (k.reserve1 + k.oldAmount);
    uint256 amount0In = k.isToken0IsIntermediaryToken ? k.oldAmount : 0;
    uint256 amount1In = k.isToken0IsIntermediaryToken ? 0 : k.oldAmount;
    uint256 amount0Out = k.isToken0IsIntermediaryToken ? 0 : k.newAmount;
    uint256 amount1Out = k.isToken0IsIntermediaryToken ? k.newAmount : 0;
    uint256 balance0Adjusted = (balance0 * 10000) - (amount0In * k.swapFee);
    uint256 balance1Adjusted = (balance1 * 10000) - (amount1In * k.swapFee);

    console.log("Flashbot --------------");
    console.log("balance 0 ", balance0);
    console.log("reserve 0 ", k.reserve0);
    console.log("amount 0 in ", amount0In);
    console.log("amount 0 out ", amount0Out);
    console.log("balance 0 ajdusted", balance0Adjusted);
    console.log("balance 1 ", balance1);
    console.log("reserve 1 ", k.reserve1);
    console.log("amount 1 in ", amount1In);
    console.log("amount 1 out ", amount1Out);
    console.log("balance 1 ajdusted", balance1Adjusted);
    console.log("swap fee ", k.swapFee);
    console.log("Left: ", balance0Adjusted * balance1Adjusted);
    console.log("Right: ", k.reserve0 * k.reserve1 * 10000**2);
    console.log(
      "Is K: ",
      balance0Adjusted * balance1Adjusted >= k.reserve0 * k.reserve1 * 10000**2
    );
    console.log("------------------------------");
    (uint256 r0, uint256 r1, ) = IWineryPair(k.poolAddress).getReserves();
    console.log("_r0 ", r0);
    console.log("_r1 ", r1);
    return balance0Adjusted * balance1Adjusted >= k.reserve0 * k.reserve1 * 10000**2;
  }

  function getAmountIn(
    uint256 amountOut,
    uint256 reserveIn,
    uint256 reserveOut,
    uint256 swapFee
  ) internal pure returns (uint256 amountIn) {
    require(amountOut > 0, "UniswapV2Library: INSUFFICIENT_OUTPUT_AMOUNT");
    require(reserveIn > 0 && reserveOut > 0, "UniswapV2Library: INSUFFICIENT_LIQUIDITY");
    uint256 numerator = reserveIn * amountOut * 10000;
    uint256 denominator = (reserveOut - amountOut) * (10000 - swapFee);
    amountIn = numerator / denominator + 1;
  }

  function withdrawFund(address erc20Address) public onlyOwner {
    uint256 balance = IERC20(erc20Address).balanceOf(address(this));
    if (balance > 0) {
      IERC20(erc20Address).transfer(owner(), balance);
    }
